class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        f = nums[0]
        for x in nums:
            f &= x
        ret = abs(f - k)
        d = {}
        for j, x in enumerate(nums):
            ks = set()
            ps = set()
            for i in range(32):
                if not (x & 1 << i):
                    ks.add(i)
                else:
                    ps.add(i)
            for q in d:
                for t in ks:
                    d[q].discard(t)
            for q in list(d.keys()):
                if not d[q]:
                    del d[q]
                else:
                    for t in d[q]:
                        ps.discard(t)
            d[j] = ps
            b = 0
            for q in sorted(d.keys()):
                for t in d[q]:
                    b |= 1 << t
                ret = min(ret, abs(k - b))
        return ret
