class Solution {
public:
    bool check(long long int  a,long long int b  ,vector<int>&nums,int n)
    {
            long long int  start = 0;
            long long int  curr = nums[0];
            int bits[31];
            memset(bits,0,sizeof(bits));
            for (int end = 0; end < n; end++) {
               curr &= nums[end];
               for(int i=0;i<31;i++)
               {
                   if((nums[end]&(1ll<<i))>0)
                       bits[i]++;
               }

                while (start < end && curr < a) {
                   long long int temp=0;
                   for(int i=0;i<31;i++)
                   {
                       if((nums[start]&(1ll<<i))>0)
                           bits[i]--;
                       if(bits[i]== end-start)
                       {
                           temp = temp|(1ll<<i);
                       }
                   }
                    curr = temp;
                    start++;
                }


                if (a <= curr && curr <= b) {
                    return true;
                }
            }

        return false;
    }
