 * @param {number} k
 * @return {number}
 */
var minimumDifference = function(nums, K) {
    const N = nums.length
    const zeroPositions = Array(31).fill(-1)
    let ans = Infinity
    for (let i = 0; i < N; i++) {
        let num = nums[i]
        check(num)
        while (num) {
            const j = findNextZero(num)
            if (j === -1) break
            num &= nums[j]
            check(num)
        }
        for (let j = 0, bit = 1; j < 31; j++, bit <<= 1) {
            if (!(nums[i] & bit)) zeroPositions[j] = i
        }
    }
    return ans
    function findNextZero(num) {
        let pos = -1
        for (let i = 0, bit = 1; i < 31; i++, bit <<= 1) {
            if ((num & bit) && zeroPositions[i] !== -1) {
                pos = Math.max(pos, zeroPositions[i])
            }
        }
        return pos
    }
    function check(num) {
        ans = Math.min(ans, Math.abs(K - num))
    }
};
