class Solution {
public:
    vector<int> b;
    int val(int r){
        int ans = 0;
        for(int i=0;i<32;i++){
            if(b[i]==r)ans = ans | (1<<i);
        }
        return ans;
    }
    void put(int x){
        for(int i=0;i<32;i++){
            if(x&(1<<i)){
                b[i]++;
            }
        }
    }

    void del(int x){
        for(int i=0;i<32;i++){
            if(x&(1<<i)){
                b[i]--;
            }
        }
    }

    int minimumDifference(vector<int>& a, int k) {
        b.resize(32,0);
        int n = a.size();

        int i=0,j=0,ans=1e9;
        while(j<n){
            put(a[j]);
            // cout<<"IN OUTER"<<i<<" "<<j<<val(j-i+1)<<endl;
            ans = min(ans,abs(val(j-i+1)-k));
            while(i<j && val(j-i+1)<k){
                del(a[i]);
                // cout<<"IN INNER"<<i<<" "<<j<<" "<<val(j-i)<<endl;
                ans = min(ans,abs(val(j-i)- k));
                i++;
            }
            j++;
        }
        return ans;
    }
};
