class Solution {
public:
    int ck[100002][30];
    int minimumDifference(vector<int>& nums, int k) {
        int i,j;
        for(i=0;i<30;i++)
        {
            ck[nums.size()][i]=nums.size();
        }
        for(i=nums.size()-1;i>=0;i--)
        {
            for(j=0;j<30;j++)
            {
                if((1LL<<j)&nums[i])
                {
                    ck[i][j]=ck[i+1][j];
                }
                else
                {
                    ck[i][j]=i;
                }
            }
        }
        int ans=INT_MAX;
        for(i=0;i<nums.size();i++)
        {
            int a=nums[i];
            ans=min(ans,abs(a-k));
            j=i;
            while(a>=k&&j<nums.size()-1)
            {
                int mn=nums.size();
                for(int l=0;l<30;l++)
                {
                    if((1LL<<l)&a)
                    {
                        mn=min(mn,ck[j][l]);
                    }
                }
                if(mn<nums.size())
                {
                    a&=nums[mn];
                    ans=min(ans,abs(a-k));
                    j=mn;
                }
                else
                {
                    break;
                }
            }
        }
        return ans;
    }
};
