class rangeAnd{
    vector<int> v;
    int cnt = 0;
    public:

    rangeAnd(){
        v.resize(31, 0);
    }

    void add(int n){
        for(int i = 0; i < 31; ++i){
            if((n >> i) & 1){
                v[i]++;
            }
        }
        ++cnt;
    }

    void remove(int n){
        for(int i = 0; i < 31; ++i){
            if((n >> i) & 1){
                v[i]--;
            }
        }
        --cnt;
    }

    int val(){
        int n = 0;
        for(int i = 0; i < 31; ++i){
            if(v[i] == cnt){
                n |= (1 << i);
            }
        }
        return n;
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();

        rangeAnd r;
        int j = 0;
        int ans = INT_MAX;
        for(int i = 0; i < n; ++i){
            r.add(nums[i]);
            ans = min(ans, abs(k - r.val()));

            while(r.val() < k && j <= i){
                ans = min(ans, abs(k - r.val()));
                r.remove(nums[j]);
                ++j;
            }

            ans = min(ans, abs(k - r.val()));
        }

        return ans;
    }
};
