class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int b[32]={};
        int ans=1e9;
        int i=0;    
        for(int j=0;j<nums.size();j++){
            int t=nums[j];
            int t1=0;
            while(t1<32){
                if((t%2) == 0) b[t1]++;
                t/=2;
                t1++;
            }

            int val=0;
            for(int i=0;i<32;i++){
                if(b[i]==0) val+=(1<<i);
            }
            ans=min(ans,abs(val-k));
            // cout<<val<<endl;
            while(i<j && val<k) {
                int t=nums[i];
                int t1=0;
                while(t1<32){
                    if((t%2) == 0) b[t1]--;
                    t/=2;
                    t1++;
                }

                val=0;
                for(int i=0;i<32;i++){
                    if(b[i]==0) val+=(1<<i);
                }
                ans=min(ans,abs(val-k));
                i++;
            }  
        }
        return ans;
    }
};
