class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        unordered_set<int> running;
        int min_diff = INT_MAX;
        for (int n: nums) {
            // check if n is in running, if it is don't bother
            unordered_set<int> new_set;
            for (int r: running) {
                new_set.insert(r & n);
            }
            new_set.insert(n);
            running = new_set;
            for (int s: running) {
                min_diff = min(min_diff, abs(k - s));
            }
        }
        return min_diff;
    }
};
