class Solution {
public:

    int t[400050];


    void build(vector<int>&a, int v, int tl, int tr) {
        if (tl == tr) {
            t[v] = a[tl];
        } else {
            int tm = (tl + tr) / 2;
            build(a, v*2, tl, tm);
            build(a, v*2+1, tm+1, tr);
            t[v] = t[v*2] & t[v*2+1];
        }
    }

    int get_max(int v, int tl, int tr, int l, int r) {
        if (l > r)
            return ((1<<30) - 1);
        if (l == tl && r == tr)
            return t[v];
        int tm = (tl + tr) / 2;
        int a = get_max(v*2, tl, tm, l, min(r, tm));
        int b = get_max(v*2+1, tm+1, tr, max(l, tm+1), r);
        return a&b;
    }


    int minimumDifference(vector<int>& v, int k) {

        int n = v.size();
        build(v,1,0,n-1);
        int ans = 1e9;
        for(int i=0;i<n;i++){
            int lo = i;
            int hi = n-1;
            while(lo<=hi){
                int mid = (lo+hi)/2;

                int x = get_max(1,0,n-1,i,mid);
                if(x == k){
                    return 0;
                }
                ans = min(ans,abs(k-x));
                if(x>k){
                    lo = mid+1;
                }else{
                    hi = mid-1;
                }
            }

        }

        return ans;
    }
};
