class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        s = set()
        s.add((1<<40) - 1)
        ans = 1e9
        for x in nums:  
            s = {y&x for y in s} | {(1<<40) - 1}
            #print(s)
            for n in s:
                ans = min(ans, abs(n-k))
        return ans
