class Solution {
public:
    int minimumDifference(vector<int>& ar, int k) {
        int n = ar.size();
        vector<vector<int>> table(n, vector<int>(20));
        for(int i = 0; i < n; i++) table[i][0] = ar[i];
        for(int j = 1; j < 20; j++) {
            for(int i = 0; i < n; i++) {
                int l = (1 << j);
                int s = l+i-1;
                if(s >= n) break;
                table[i][j] = (table[i][j-1] & table[i+l/2][j-1]);
            }
        }


        int ans = abs(k-ar[0]);
        for(int i = 0; i < n; i++) {
            int l = i, r = n-1;
            ans = min(ans, abs(k-ar[i]));
            while(l < r) {
                int mid = (l+r)/2;
                int len = mid-i+1;
                int lg = log2(len);
                int lg2 = (1 << lg);
                int av = (table[i][lg] & table[mid-lg2+1][lg]);
                ans = min(ans, abs(k-av));
                if(av <= k) {
                    r = mid;
                }
                else {
                    l = mid+1;
                }
            }
            for(int mid: {l-1, l, l+1}) {
                if(mid < i || mid >= n) continue;
                int len = mid-i+1;
                int lg = log2(len);
                int lg2 = (1 << lg);
                int av = (table[i][lg] & table[mid-lg2+1][lg]);
                ans = min(ans, abs(k-av));
            }
        }
        return ans;
    }
};
