class Solution {
public:
    #define MAX 100005
    #define bitscount 32
    int prefix_count[bitscount][MAX];

// Function to find the prefix sum
    void findPrefixCount(vector<int> arr, int n)
    {

        // Loop for each bit
        for (int i = 0; i < bitscount; i++) {

            // Loop to find prefix count
            prefix_count[i][0] = ((arr[0] >> i) & 1);
            for (int j = 1; j < n; j++) {
                prefix_count[i][j] = ((arr[j] >> i) & 1);
                prefix_count[i][j] += prefix_count[i][j - 1];
            }
        }
    }
    int rangeAnd(int l, int r)
    {

        // To store the answer
        int ans = 0;

        // Loop for each bit
        for (int i = 0; i < bitscount; i++) {
            // To store the number of variables
            // with ith bit set
            int x;
            if (l == 0)
                x = prefix_count[i][r];
            else
                x = prefix_count[i][r]
                    - prefix_count[i][l - 1];

            // Condition for ith bit
            // of answer to be set
            if (x == r - l + 1)
                ans = (ans | (1 << i));
        }

        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int ans=1e9;
        int l=0,r=1e9,n=nums.size();
        findPrefixCount(nums, n);
        while(l<=r){
            int m= l+(r-l)/2;
            int i=0,j=0;
            int nd=(1<<30)-1,ch=0;
            map<int,int> mp;
            while(j<n){
                nd= rangeAnd(i,j);
                int diff= abs(nd-k);
                if(diff<=m){
                    ch=1; break;
                }
                else{
                    if(nd>k){
                        j++;
                    }
                    else{
                        i++;
                        if(j<i)j++;
                    }
                }
            }
            if(ch==1){
                ans=m; r=m-1;
            }
            else l=m+1;
        }
        return ans;
    }
};
