int pre[100005][30]={0};
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        for(int i=0;i<n;i++)
            for(int j=0;j<30;j++){
                pre[i+1][j]=pre[i][j];
                if((nums[i]&(1<<j))) pre[i+1][j]++;
            }
        int ans=1e9;
        for(int i=0;i<n;i++){
            int l=i,r=n-1,diff=1e9;
            while(l<=r){
                int mid=(l+r)/2,val=0;
                for(int j=0;j<30;j++) if(pre[mid+1][j]-pre[i][j]==mid-i+1) val+=(1<<j);
                diff=min(diff,abs(k-val));
                if(val>k) l=mid+1;
                else r=mid-1;
            }
            ans=min(ans,diff);
        }
        return ans;
    }
};
