class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        cur = set()
        res = float('inf')
        for num in nums:
            cur = {num & tmp for tmp in cur} | {num}
            cur_min_res = min(abs(tmp - k) for tmp in cur)
            res = min(res, cur_min_res)
        return res
