class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        ans = 10**10

        i,n = 0, len(nums)
        f = Counter()

        def ad(x):
            for i in range(32):
                if (x&(1<<i)):
                    f[i]+=1
        def rem(x):
            for i in range(32):
                if (x&(1<<i)):
                    f[i]-=1
        def num(mx):
            ans = 0
            if mx ==0:
                return 0
            for i in range(32):
                if f[i]==mx:
                    ans+=1<<i
            return ans

        for j,x in enumerate(nums):
            ad(x)
            while i<=j and num(j-i+1)<k:
                ans = min(ans, k-num(j-i+1))
                rem(nums[i])
                i+=1
            if i<=j:    
                ans = min(ans, abs(num(j-i+1)-k))


        return ans
