typedef long long ll;
typedef vector<int> vll;
class Solution {
public:
    void build(ll i, ll l, ll r, vll &arr, vll &tree){
        if(l == r){
            tree[i] = arr[l];
            return;
        }
        ll mid = (l+r)>>1;
        build(2*i, l, mid,arr,tree);
        build(2*i+1, mid+1,r,arr,tree);
        tree[i] = tree[2*i]&tree[2*i+1];
    }

    int query(ll i, ll l, ll r, ll lq, ll rq,vll &arr, vll &tree){
        if(rq<l || lq>r) return INT_MAX;
        if(lq<=l && r<=rq) return tree[i];

        ll mid = (l+r)>>1;

        return query(2*i,l,mid,lq,rq,arr,tree) &query(2*i+1,mid+1,r,lq,rq,arr,tree);
    }

    int minimumDifference(vector<int>& arr, int k) {
        int n = arr.size();
        vector<int> tree(4*n);
        build(1,0,n-1,arr,tree);
        int ans = INT_MAX;
        for (int i = 0; i < n; i++) {
            int lo=i, hi=n-1;
            int temp = INT_MAX;
            while(lo <= hi){
                int mid = (lo+hi)>>1;
                int q = query(1,0,n-1,i,mid,arr,tree);
                if(q >= k){
                    temp = min(temp,q-k);
                    lo = mid+1;
                }else{
                    temp = min(temp,k-q);
                    hi = mid-1;
                }
            }
            ans = min(ans,temp);

        }
        return ans;
    }
};
