class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector <int> last(30, -1);
        int res = 2e9;
        for (int i = 0; i < nums.size(); ++i){
            vector <int> pos;
            for (int j = 0; j < 30; ++j){
                if (last[j] != -1){
                    pos.push_back(last[j]);
                }
            }
            sort(pos.rbegin(), pos.rend());
            int val = nums[i];
            res = min(res, abs(val - k));
            for (auto &j: pos){
                val &= nums[j];
                res = min(res, abs(val - k));
            }
            for (int j = 0; j < 30; ++j){
                if (~nums[i] >> j & 1){
                    last[j] = i;
                }
            }
        }
        return res;
    }
};
