class Solution {
public:
    int helper(int s,int e,vector<vector<int>>&dontHave,int n){
        int ans=0;

            for(int i=0;i<32;i++){
                if(s>0){
                    if(dontHave[e][i]==dontHave[s-1][i])ans|=(1<<i);
                }else{
                    if(dontHave[e][i]==0)ans|=(1<<i);
                }
            }
        return ans;


    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>dontHave(n);
        vector<int>count(32,0);
        for(int i=0;i<n;i++){
            for(int j=0;j<32;j++){
                if(nums[i]&(1<<j)){

                }else{
                    count[j]++;
                }
            }
            dontHave[i]=count;
        }
        int mn=1e9;
        for(int i=0;i<n;i++){
            int s=i;
            int diff = abs(k-nums[i]);
            int e=n-1;
            while(s<=e){
                int mid = s+(e-s)/2;
                int subAnd = helper(i,mid,dontHave,n);
                if(subAnd>k){
                    diff=min(diff,subAnd-k);
                    s=mid+1;
                }else if(subAnd<k){
                    diff=min(diff,k-subAnd);

                    e=mid-1;
                }else{
                    return 0;
                }
            }
            mn=min(mn,diff);
        }
        return mn;
    }
};
