class Solution {
public:
    int st[200005][19];
    int lg[200009];

    int query(int L, int R){
        int i = lg[R - L + 1];
        int minimum = (st[L][i]&st[R - (1 << i) + 1][i]);
        return minimum;
    }


    int minimumDifference(vector<int>& a, int k) {
        int i,j,l,m,n=a.size();


        lg[1] = 0;
        for (int i = 2; i <=200008; i++)
        lg[i] = lg[i/2] + 1;

        for(i=0;i<n;i++){
            st[i][0]=a[i];
        }

        for (int i = 1; i < 19; i++)
        for (int j = 0; j + (1 << i) <= n; j++)
        st[j][i] = (st[j][i - 1]&st[j + (1 << (i - 1))][i - 1]);

        int ans=2000000000;
        for(i=0;i<n;i++){
            int hi=n-1,lo=i, cur_ans=-1;
            while(lo<=hi){
                int mid=(hi+lo)/2;
                int q=query(i,mid);
                if(q<=k){
                    cur_ans=q;
                    //cout<<i<<" "<<mid<<" "<<q<<"\n";
                    hi=mid-1;
                }
                else lo=mid+1;
            }
            if(cur_ans==-1){
                ans=min(ans,abs(k-query(i,n-1)));
            }
            else{
                ans=min(ans,abs(k-cur_ans));
            }

            lo=i;
            hi=n-1;
            cur_ans=-1;
            while(lo<=hi){
                int mid=(hi+lo)/2;
                int q=query(i,mid);
                if(q>=k){
                    cur_ans=q;
                    //cout<<i<<" "<<mid<<" "<<q<<"\n";
                    lo=mid+1;
                }
                else hi=mid-1;
            }
            if(cur_ans==-1){
                ans=min(ans,abs(k-a[i]));
            }
            else{
                ans=min(ans,abs(k-cur_ans));
            }
        }

        return ans;
    }
};
