class Solution {
public:
    void put(vector<vector<int>>&freq, int num, int id){
        int cid =0;
        if(id>0)
        for(int i=0;i<31;i++){
            freq[id][i]=freq[id-1][i];
        }
        while(num){     
            if(num%2)freq[id][cid]+=1;
            num/=2;
            cid++;
        }
    }
    int bs(vector<vector<int>>&freq, int k, int sid){
        int st = sid, end = freq.size()-1, ans =sid;
        while(st<=end){
            int mid = (st+end)/2;
            int obt = 0;
            for(int i=0;i<31;i++){
                int f = freq[mid][i];
                if(sid>0) f-=freq[sid-1][i];
                if(f==(mid-sid+1)) obt|=(1<<i);
            }
            if(obt>=k){
                ans=mid;
                st=mid+1;
            }
            else{
                end = mid-1;
            }
        }
        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>freq(n, vector<int>(31, 0));
        for(int i=0;i<n;i++){
            put(freq, nums[i], i);
        }
        int ans = INT_MAX;
        for(int i=0;i<n;i++){
            int midx = bs(freq, k, i);
            // cout<<midx;
            // int midx = 0;

            int obt =0;
            for(int j=0;j<31;j++){
                int f = freq[midx][j];
                if(i>0) f-=freq[i-1][j];
                if(f==(midx-i+1)) obt|=(1<<j);
            }
            // cout<<i<<" "<<midx<<" "<<obt<<endl;
            ans = min(ans, abs(k-obt));
            midx++;
            obt =0;
            if(midx<n){
                for(int j=0;j<31;j++){
                    int f = freq[midx][j];
                    if(i>0) f-=freq[i-1][j];
                    if(f==(midx-i+1)) obt|=(1<<j);
                }
                // cout<<i<<" "<<midx<<" "<<obt<<endl;
                ans = min(ans, abs(k-obt));
            }
        }
        return ans;
    }
};
