using ll=long long;
class Solution {
public:
  int cnt=0;
       void add(vector<ll> &arr,int num)
    {
      int k=0;
      while(num!=0)
      {
        if(num%2==1){arr[k]++;}
        num/=2;
        k++;
      }
         cnt++;
    }
    void del(vector<ll> &arr,int num)
    {
         int k=0;
      while(num!=0)
      {
        if(num%2==1){arr[k]--;}
        num/=2;
        k++;
      }
      cnt--;
    }

  int cal(vector<ll> &arr)
  {  int num=0;
    for(int i=0;i<40;i++)
    { if(arr[i]==cnt&&cnt>0){
      num+=pow(2,i);}
    }
   return num;
  }
  void clear(vector<ll> &arr)
  {
    for(int i=0;i<40;i++)
    {
      arr[i]=0;
    }
    cnt=0;
  }
    int minimumDifference(vector<int>& nums, int k) {
        ll n=nums.size();
        ll ans=INT_MAX;
      ll i=0,j=0;
      vector<ll> arr(40,0);
      while(i<=j&&j<n)
      { 
        add(arr,nums[j]);
        ll var = cal(arr);
        ans=min(ans,abs(k-var));
        if(var==k){return 0;}
        if(var<k)
        {  
          while(var<k&&i<=j)
          {
            del(arr,nums[i]);
            // cout<<"here "<<i<<" "<<j<<endl;
            var=cal(arr);
            // cout<<"here "<<i<<" "<<j<<endl;
            if(cnt>0){
            ans=min(ans,abs(k-var));}
            // cout<<"here "<<i<<" "<<j<<endl;
            i++;
          }
            // cout<<"here "<<i<<" "<<j<<endl;
        }
        if(j==n-1)
        {
          while(i<=j)
          {
            del(arr,nums[i]);
            var=cal(arr);
            if(cnt>0){
            ans=min(ans,abs(k-var));}
            i++;
          }
          break;
        }
        // cout<<"here "<<i<<" "<<j<<endl;
        j++;
      }
      return ans;
    }
};
