class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        a.push_back(0);
        reverse(a.begin(),a.end());
        // for(auto x:a)
        //     cout<<x<<" ";
        // cout<<"\n\n";
        int n=a.size();
        int v[32][n];
        int j=0;
        while(j<32)
        {
            v[j][0]=0;
            j++;
        }
        int i=1;
        while(i<a.size())
        {
            int j=0;
            while(j<32)
            {
                v[j][i]=v[j][i-1];
                if((a[i]&(1ll<<j)))
                {
                    // cout<<"yes "<<j<<" "<<i<<"\n";
                    v[j][i]++;
                }
                j++;
            }
            i++;
        }
        // i=1;
        // while(i<2)
        // {
        //     cout<<a[i]<<"\n";
        //     int j=0;
        //     while(j<3)
        //     {
        //         cout<<j<<" "<<v[j][i]<<"\n";
        //         j++;
        //     }
        //     i++;
        // }
        int ans=INT_MAX;
        i=1;
        while(i<a.size())
        {
            int s=i,e=a.size()-1;
            while(s<=e)
            {
                int m=(s+e)/2;
                int x=0;
                int j=0;
                while(j<32)
                {
                    // cout<<v[j][m]<<" "<<v[j][i-1]<<"\n";
                    if(v[j][m]-v[j][i-1]==m-i+1)
                        x+=1<<j;
                    j++;
                }
                // cout<<m<<" "<<x<<"\n";
                ans=min(ans,abs(k-x));
                if(x>=k)
                    e=m-1;
                else
                    s=m+1;
            }
            s=i;
            e=a.size()-1;
            while(s<=e)
            {
                int m=(s+e)/2;
                int x=0;
                int j=0;
                while(j<32)
                {
                    if(v[j][m]-v[j][i-1]==m-i+1)
                        x+=1<<j;
                    j++;
                }
                ans=min(ans,abs(k-x));
                if(x<=k)
                    e=m-1;
                else
                    s=m+1;
            }
            i++;
        }
        return ans;
    }
};
