struct SparseTable{
    vector<vector<int>> table;
    SparseTable(vector<int> nums){
        table.resize(nums.size(),vector<int> (20));
        for (int i = 0; i < nums.size(); i++){
            table[i][0]=nums[i];
        }
        for (int j = 1; j < 20; j++){
            for (int i = 0; i+(1<<j)<=nums.size();i++){
                table[i][j]=table[i][j-1]&table[i+(1<<(j-1))][j-1];
            }
        }
    }
    int query(int left, int right){
        int length = right-left+1;
        int bit = 31-__builtin_clz(length);
        return table[left][bit]&table[right-(1<<bit)+1][bit];
    }
};
class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        SparseTable st(nums);
        int minDiff = INT_MAX;
        for (int i = 0; i < nums.size(); i++){
            int lp = i; int rp = nums.size()-1;
            int ans = INT_MAX;
            while (lp<=rp){
                int mid = (lp+rp)>>1;
                int r = st.query(i,mid);
                ans = min(ans,abs(k-r));
                if (r>=k){
                    lp=mid+1;
                }
                else{
                    rp=mid-1;
                }
            }
            minDiff=min(minDiff,ans);
        }
        return minDiff;
    }
};
