class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int ans=INT_MAX;
        vector<int> bin(30,0);
        int cur=((1<<30)-1);
        int i=0,j=0;
        while(j<nums.size()){
            for(int l=0;l<30;l++){
                if(((1<<l)&nums[j])==0){
                    bin[l]++;
                    if(bin[l]==1){
                        cur-=(1<<l);
                    }
                }
            }
            ans=min(ans,abs(cur-k));
            if(cur==k) return 0;
            if(cur>k){
                j++;
            }
            else{
                while(cur<k){
                    for(int l=0;l<30;l++){
                        if(((1<<l)&nums[i])==0){
                            bin[l]--;
                            if(bin[l]==0){
                                cur+=(1<<l);
                            }
                        }
                    }
                    ans=min(ans,abs(cur-k));
                    if(cur==k) return 0;
                    i++;
                }
                j++;
            }
        }
        return ans;
    }
};
