class Solution {
public:
    static const int N=2e5+5;
    int mn[N][20],lg[N];
    int find(int x ,int y){
        int len=lg[y-x+1];
		int ans=mn[x][len]&mn[y-(1<<len)+1][len];
        return ans;
    }
    int minimumDifference(vector<int>& a, int k) {
        int n=a.size();
        for(int i=1;i<=n;i++){
		lg[i]=(int)log2(i);
	}
	for(int i=0;i<n;i++){
		mn[i][0]=a[i];
	}
	for(int j=1;j<20;j++){
		for(int i=0;i<n;i++){
			if((1<<(j-1))+i>n-1)continue;
			mn[i][j]=(mn[i][j-1]&mn[i+(1<<(j-1))][j-1]);
		}
	}
	    int ans=1e9+2;
        for(int i=0;i<n;i++){
            int low=i,high=n-1,ind=n-1;
            while(low<=high){
                int mid=(low+high)/2;
                int p=find(i,mid);
                if(p>=k){
                    low=mid+1;
                    ind=mid;
                }
                else high=mid-1;
            }
            ans=min(ans,abs(find(i,ind)-k));
            low=i,high=n-1;
                int ind1=n-1;
            while(low<=high){
                int mid=(low+high)/2;
                int p=find(i,mid);
                if(p<=k){
                    high=mid-1;
                    ind1=mid;
                }
                else low=mid+1;
            }
            // cout<<ind<<" "<<ind1<<endl;
            ans=min(ans,abs(find(i,ind1)-k));
        }
        return ans;
    }
};
