class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        long long n = nums.size();
        vector<map<long long,long long>> mp(n);
        mp[n-1][nums[n-1]]++;
        map<long long,long long> mp2;
        mp2[nums[n-1]]++;
        for(int i=n-2;i>=0;i--){
            mp[i][nums[i]]++;
            for(auto j : mp[i+1]){
                mp[i][(j.first)&nums[i]]+=j.second;
            }
            for(auto j : mp[i]){
                mp2[j.first]+=j.second;
            }
        }

        long long mini = 1e9+7;
        for(auto [val,_] : mp2){
            mini=min(mini,abs(k-val));
        }

        return (int)mini;



    }
};
