class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        res = -1
        def update(v):
            nonlocal res
            if res == -1 or res > abs(k-v):
                res = abs(k-v)

        suf = set()
        for item in nums:
            cur = set()
            for s in suf:
                v = s&item
                update(v)
                cur.add(v)
            update(item)
            cur.add(item)
            suf = cur
        return res
