class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        ret=pow(10,10)
        s=set()
        for num in nums:
            nexts=set()
            ret=min(ret,abs(num-k))
            if ret==0:
                return 0
            if num>k:
                nexts.add(num)
            for n in s:
                n&=num
                ret=min(ret,abs(n-k))
                if ret==0:
                    return 0
                if n>k:
                    nexts.add(n)
            s=nexts
        return ret
