class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {

        int x=nums[0],ans=abs(x-k),p,i,j=0;
        vector<int> cnt(32);
        for(i=0;i<nums.size();i++)
        {
            if(j<i)
            {
                j=i;
                x=nums[i];
                ans=min(ans,abs(x-k));
                for(p=0;p<32;p++)
                cnt[p]=0;
            }
            // cout<<x<<" ";
            ans=min(ans,abs(x-k));
            while(j<nums.size()&&x>k)
            {
                x=x&nums[j];
                ans=min(ans,abs(x-k));
                for(p=0;p<31;p++)
                {
                    if(((nums[j]>>p)&1)==0)
                    {
                        cnt[p]++;
                    }
                }
                j++;
            }
            // cout<<x<<" ";
            if(j<=i)
                continue;
            for(p=0;p<31;p++)
            {
                if(((nums[i]>>p)&1)==0)
                {
                    cnt[p]--;
                    if(cnt[p]==0)
                    {
                        x+=powl(2,p);
                    }
                }
            }
        }
        return ans;
    }
};
