class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        nums = [n for n,_ in itertools.groupby(nums)]
        cache = [set() for _ in range(len(nums))]
        res = math.inf
        for idx in range(len(nums)):
            curr = nums[idx]
            for j in range(idx, len(nums)):
                curr &= nums[j]
                if curr in cache[j]:
                    break
                cache[j].add(curr)
                res = min(res, abs(k-curr))
                if curr==0 or curr < k-res:
                    break
            if res==0:
                return 0
        return res
