class Segment_Tree
{
private:
    vector<int> seg_tree;
    int arr_size;

    void build(int low, int high, int seg_index, vector<int> &nums)
    {
        if (low == high)
        {
            seg_tree[seg_index] = nums[low];
            return;
        }

        int mid = low + (high - low) / 2;

        build(low, mid, 2 * seg_index + 1, nums);
        build(mid + 1, high, 2 * seg_index + 2, nums);

        seg_tree[seg_index] = seg_tree[2 * seg_index + 1] & seg_tree[2 * seg_index + 2];
    }

    int query(int low, int high, int seg_index, int start, int end)
    {
        if (start <= low && end >= high)
            return seg_tree[seg_index];

        if (start > high || end < low)
            return INT_MAX;

        int mid = low + (high - low) / 2;

        int left = query(low, mid, 2 * seg_index + 1, start, end);
        int right = query(mid + 1, high, 2 * seg_index + 2, start, end);

        return left & right;
    }

public:
    Segment_Tree(vector<int> &nums)
    {
        arr_size = nums.size();
        seg_tree.resize(4 * arr_size + 1);
        build(0, arr_size - 1, 0, nums);
    }

    int query(int start, int end)
    {
        if (start > end)
            return INT_MAX;

        return query(0, arr_size - 1, 0, start, end);
    }
};

class Solution {
private:
    int binSearch(int low, int high, Segment_Tree &st, const int k)
    {
        int originalLow = low;

        while (low <= high)
        {
            int mid = low + (high - low) / 2;

            int andd = st.query(originalLow, mid);

            if (andd == k)
                return k;

            if (andd > k)
                low = mid + 1;
            else
                high = mid - 1;
        }

        int l = st.query(originalLow, low);
        int h = st.query(originalLow, high);

        if (l == INT_MAX)
            return h;

        if (h == INT_MAX)
            return l;

        if (abs(l - k) < abs(h - k))
            return l;

        return h;
    }

public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size(), answer = INT_MAX;
        Segment_Tree st(nums);

        for (int i = 0; i < n; i++)
        {
            int find = binSearch(i, n - 1, st, k);
            answer = min(answer, abs(k - find));
        }

        return answer;
    }
};
