class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int ret = INT_MAX, t = nums[0];
        int n = nums.size();
        vector<vector<int>> a(n, vector<int>(30));
        vector<int> v(30);

        for (int d = 0; d < 30; ++d) {
            if (~k>>d&1) continue;
            v[d] = 1;
        }

        for (int i = 0; i < n; ++i) {
            for (int d = 0; d < 30; ++d) {
                if (~nums[i]>>d&1) continue;
                a[i][d] = 1;
            }
        }

        int j = 0;
        vector<int> cnt(30);

        for (int i = 0; i < n; ++i) {
            if (i > 0) {
                for (int d = 0; d < 30; ++d) {
                    cnt[d] -= a[i - 1][d];
                }
            }

            while (j <= i) {
                for (int d = 0; d < 30; ++d) {
                    cnt[d] += a[j][d];
                }
                j += 1;
            }    

            while (true) {
                int t = 0;
                for (int d = 0; d < 30; ++d) {
                    if (cnt[d] < j - i) continue;
                    t += (1 << d);
                }
                ret = min(ret, abs(k - t));
                if (t < k or j == n) break;

                for (int d = 0; d < 30; ++d) {
                    cnt[d] += a[j][d];
                }
                j += 1;
            }
        }

        return ret;
    }
};
