class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {

        int n=nums.size();

         vector<vector<int>>st(n,vector<int>(21));

    for(int i=0;i<n;i++)
    st[i][0]=nums[i];

    for(int k=1;k<=20;k++){
        for(int i=0;i+(1<<k)-1<n;i++){
            st[i][k]=(st[i][k-1]&st[i+(1<<(k-1))][k-1]);

        }
    }

    auto query=[&](int a,int b){

        int dx=(b-a);
        if(dx==0)
            return nums[a];
    int k=floor(log2(dx));
    int ans=(st[a][k]&st[b-(1<<k)+1][k]);
    return ans;
    };


        int ans=INT_MAX;
        for(int i=0;i<n;i++){

            int l=i,r=n;

            if(nums[l]<k){
              ans=min(ans,abs(k-nums[l]));
                continue;
            }

            while(r>l+1){
                int m=(l+r)/2;

                int cur = query(i,m);
                if(cur>=k)
                    l=m;
                else
                    r=m;
            }

            ans=min(ans,abs(k-query(i,l)));

            if(r<n)
                ans=min(ans,abs(k-query(i,r)));


        }

        return ans;





    }
};
