class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int l=0,r=0,n=nums.size();
        int temp,temp2,ans=INT_MAX;
        int bitcnt[32]={0};
        temp=nums[0];
        for(int i=0;i<32;i++){
             bitcnt[i]+=temp%2;
            temp/=2;
        }
        while(r<n){
            temp=0;
            for(int i=0;i<32;i++){
                if(bitcnt[i]==r-l+1) temp+=(1<<i);
            }
            ans=min(ans,abs(temp-k));
            if(ans==0) return 0;
            if(temp>k){
                r++;
                if(r<n){
                    temp2=nums[r];
                    for(int i=0;i<32;i++){
                        bitcnt[i]+=temp2%2;
                        temp2/=2;
                    }

                }
            }
            else{

                if(l<n){
                    temp2=nums[l];
                    for(int i=0;i<32;i++){
                        bitcnt[i]-=temp2%2;
                        temp2/=2;
                    }

                }
                l++;
                if(r<l){
                    if(l<n){
                       temp2=nums[l];
                        for(int i=0;i<32;i++){
                            bitcnt[i]+=temp2%2;
                            temp2/=2;
                        } 
                    }     
                    r=l;
                }
            }
        }
        return ans;
    }
};
