class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        int n=a.size();
        vector<vector<int>> z(n,vector<int> (32));
        for(int i=0;i<n;i++){
            int x=a[i];
            for(int j=0;j<32;j++){
                z[i][j]=x%2;
                x/=2;
            }
        }
        for(int i=1;i<n;i++){
            for(int j=0;j<32;j++){
                z[i][j]+=z[i-1][j];
            }
        }
        int ans=INT_MAX;
        for(int i=0;i<n;i++){
            int l=i,h=n-1;
            while(l<=h){
                int mid=(l+h)/2;
                int x=0;
                if(i==0){
                    for(int j=0;j<30;j++){
                        if(z[mid][j] == mid+1) x+=(1<<j);
                    }
                }
                else{
                    for(int j=0;j<30;j++){
                        if(z[mid][j]-z[i-1][j] == mid-i+1) x+=(1<<j);
                    }
                }
                if(x==k) return 0;
                if(x>k){
                    ans=min(ans,abs(x-k));
                    l=mid+1;
                }
                else{
                    ans=min(ans,abs(x-k));
                    h=mid-1;
                }
            }
        }
        return ans;
    }
};
