class SGT
{
public:
    int n;
    vector<int> t;
    SGT(vector<int> &a)
    {
        n = (int)a.size();
        t.resize(4 * n);
        build(a, 1, 0, n - 1);
    }

    int combine(int a, int b)
    {
        return a & b;
    }

    void build(vector<int> &a, int v, int tl, int tr)
    {
        if (tl == tr)
        {
            t[v] = a[tl];
        }
        else
        {
            int tm = (tl + tr) / 2;
            build(a, v * 2, tl, tm);
            build(a, v * 2 + 1, tm + 1, tr);
            t[v] = combine(t[v * 2], t[v * 2 + 1]);
        }
    }

    void update(int v, int tl, int tr, int pos, int new_val)
    {
        if (tl == tr)
        {
            t[v] = new_val;
        }
        else
        {
            int tm = (tl + tr) / 2;
            if (pos <= tm)
                update(v * 2, tl, tm, pos, new_val);
            else
                update(v * 2 + 1, tm + 1, tr, pos, new_val);
            t[v] = combine(t[v * 2], t[v * 2 + 1]);
        }
    }
    int query(int l, int r)
    {
        return query(1, 0, n - 1, l, r);
    }
    void update(int pos, int new_val)
    {
        update(1, 0, n - 1, pos, new_val);
    }
    int query(int v, int tl, int tr, int l, int r)
    {
        if (l > r)
            return -1;
        if (l == tl && r == tr)
        {
            return t[v];
        }
        int tm = (tl + tr) / 2;
        return combine(query(v * 2, tl, tm, l, min(r, tm)), query(v * 2 + 1, tm + 1, tr, max(l, tm + 1), r));
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        int INF = 1e9;
        int n = a.size();
        a.push_back(0);
        SGT sgt(a);
        int ans = INF;
        for(int l=0;l<n;l++) {
            int beg = l, end = n-1;
            while(beg <= end) {
                int mid = (beg + end)/2;
                int curr = sgt.query(l, mid);
                int val = abs(curr - k);
                ans = min(ans, val);
                if(curr < k) {
                    end = mid-1;
                }
                else {
                    beg = mid+1;   
                }
            }
        }
        return ans;
    }
};
