class Solution {
public:
    long long binpow(long long a, long long b) {
    long long res = 1;
    while (b > 0) {
        if (b & 1)
            res = res * a;
        a = a * a;
        b >>= 1;
    }
    return res;
}

    int minimumDifference(vector<int>& nums, int k) {
        vector<int> pos(31, 0);
        long long n = nums.size(), j = 0, ans = INT_MAX, cur = INT_MAX;
        for(int i = 0; i < n; i++)
        {
            string x = bitset<31>(nums[i]).to_string();
            reverse(x.begin(), x.end());
            // cout << x << "\n";
            for(int l = 0; l < 31; l++)
            {
                if(x[l] == '1')
                {
                    pos[l]++;
                }
                long long y = binpow(2, l);
                if(pos[l] == i-j+1)
                {
                    cur |= y;
                }
                else
                {
                    long long z = cur|y;
                    if(z == cur)
                    {
                        cur = cur^y;
                    }    
                }
            }
            if(cur < k)
            {
                ans = min(ans, k-cur);
                while(i >= j && cur < k)
                {
                    string x = bitset<31>(nums[j]).to_string();
                    reverse(x.begin(), x.end());
                    for(int l = 0; l < 31; l++)
                    {
                        if(x[l] == '1')
                        {
                            pos[l]--;
                        }
                        long long y = binpow(2, l);
                        if(pos[l] == i-j)
                        {
                            cur |= y;
                        }
                        else
                        {
                            long long z = cur|y;
                            if(z == cur)
                            {
                                cur = cur^y;
                            }
                        }
                    }
                    j++;
                    ans = min(ans, abs(k-cur));
                }
            }
            ans = min(ans, abs(k-cur));
            ans = min(ans, abs((long long)k-nums[i]));
        }
        return ans;
    }
};
