class Solution {
public:

    int Calc(vector<vector<int>>&bits, int start, int end) {
        if(start > end) return 2e9+1; 
        vector<int>cnt(32);
        int a = end - start + 1;
        for(int i=0; i<32; i++) {
            cnt[i] = bits[end][i];
            if(start != 0) cnt[i] -= bits[start-1][i];
        }
        int val = 0;
        for(int i=0; i<32; i++) {
            if(cnt[i] == a) val += (1<<i);
        }
        return val;
    }

    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int start = 0, end = 0;
        int cnt = 0;
        int ans = 1e9;

        vector<vector<int>>bits(n,vector<int>(32));
        for(int i=0; i<n; i++) {
            for(int j=0; j<32; j++) {
                if(nums[i] & (1<<j)) {
                    bits[i][j]++;
                }
            }
        }

        for(int i=1; i<n; i++) {
            for(int j=0; j<32; j++) bits[i][j] += bits[i-1][j];
        }

        int temp = 0;

        while(start < n) {

            if(end < n && cnt == 0) {
                temp = nums[end];
                end++;
                ans = min(ans,abs(k-temp));
                cnt++;
            } 
            else if(end < n && temp >= k) {
                temp = (temp & nums[end]);
                end++;
                ans = min(ans, abs(k-temp));
                cnt++;
                // int num = Calc(bits,start,end);
            }
            else if(start < n) {
                temp = Calc(bits,start+1,end-1);
                start++;
                cnt--;
                ans = min(ans, abs(k-temp));
            }
        }
        return ans;
    }
};
