class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        long long lim=31,n=nums.size(),ans=INT_MAX;
        vector<vector<long long>> pre(lim,vector<long long>(n,0));

        for(long long i=0;i<n;i++)
        {
            set<int>pos;
            pos.insert(1);pos.insert(i+1);
            for(long long j=0;j<lim;j++)
            {
                if((1LL<<j)&nums[i]){
                    pre[j][i]=1;
                    if(i)
                        pre[j][i]+=pre[j][i-1];
                    pos.insert(pre[j][i]);
                    pos.insert(min(pre[j][i]+1,i+1));
                    pos.insert(max(pre[j][i]-1,1LL));
                }
            }
            for(auto len:pos)
            {
                long long cur=0;
                for(long long j=0;j<lim;j++)
                {
                    if(len<=pre[j][i])
                        cur|=(1LL<<j);
                }
                ans=min(ans,abs(cur-k));
            }
        }
        return ans;
    }
};
