class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        if (nums.size() == 1)
            return abs(nums[0] - k);


        unordered_set<int> current;
        int ans=INT_MAX;

        for (int num : nums) {
            unordered_set<int> newCurrent;
            newCurrent.insert(num);
            for (int val : current) {
                newCurrent.insert(val & num);
            }
            for (int val : newCurrent) {
                ans=min(ans,abs(val-k));
            }
            current = newCurrent;
        }
        return ans;     
    }
};
