class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> u,v;
        int ret =1e9,sz=0;
        for(int x: nums){
            for(int &y: u)y&=x;
            u.push_back(x);
            vector<int>v;
            for(int y: u){
                ret=min(ret,abs(y-k));
                if(v.empty()||v.back()!=y)v.push_back(y);
            }
            u.swap(v);
        }
        return ret;
    }
};
