class Solution {
public:
int minimumDifference(vector<int>& nums, int k) {
    int res = INT_MAX;
    vector<int> dp(nums.size(), 0);
    for (int i = 0; i < nums.size(); ++i) {
        int val = nums[i];
        // cout << i << ": ";
        for (int j = i; j < nums.size(); ++j) {
            val &= nums[j];
            // cout << val << " ";
            res = min(res, abs(k - val));
            if (val < k && (k - val) >= res)
                break;
            if (val <= dp[j])
                break;
            dp[j] = val;
        }
        // cout << endl;
    }
    return res;
}
};
