class Solution {
public:
    int abs(int a){ return a>0?a:-a;}
    int minimumDifference(vector<int>& nums, int k) {
        int j=1,ct=0,mx=0;
        vector<int> ad;
        for(auto it:nums){
            mx=max(mx,it);
        }
        while(j<=mx){
            ad.push_back(j);
            j<<=1;
            ct++;
        }
        j>>=1;
        vector<int> lz(ct,-1);
        int t,l,ans=INT_MAX,pr;
        for(int i=0;i<nums.size();i++){
            t=j;
            l=0;
            while(t){
                if(!(t&nums[i])) lz[l]=i;
                l++;
                t>>=1;
            }
            t=(j<<1)-1;
            //cout<<t<<' ';
            vector<pair<int,int>> h;
            for(int j=0;j<lz.size();j++){
                if(lz[j]==i) t-=ad[ct-j-1];
                else if(lz[j]!=-1)h.push_back({i-lz[j],j});
            }
            sort(h.begin(),h.end());
            pr=-1;
            for(auto it:h){
                if(it.first!=pr){
                    ans=min(ans,abs(t-k));
                }
                pr=it.first;
                t-=ad[ct-it.second-1];
            }
            ans=min(ans,abs(t-k));
        }
        return ans;
    }
};
