// 1 - Bases Indexing
template <class T> struct Seg {  // comb(ID,b) = b
    const T ID = -1; // change it ATQ
    T comb(T a, T b) { return a & b; } // change it ATQ
    int n;
    vector<T> seg;
    Seg(int _n) {
        n = _n;
        seg.assign(2 * n, ID);
    }
    void pull(int p) { seg[p] = comb(seg[2 * p], seg[2 * p + 1]); }
    void upd(int p, T val) {  
        seg[p += n] = val;
        for (p /= 2; p; p /= 2) pull(p);
    }
    T query(int l, int r) {  
        T ra = ID, rb = ID;
        for (l += n, r += n + 1; l < r; l /= 2, r /= 2) {
            if (l & 1) ra = comb(ra, seg[l++]);
            if (r & 1) rb = comb(seg[--r], rb);
        }
        return comb(ra, rb);
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& arr, int k) {
        int ans = 1e9 + 1 , n = arr.size();
        Seg<int> st(n + 1);
        for(int i = 0 ; i < n ; i++) {
            st.upd(i + 1 , arr[i]);
        }
        for(int i = 1 ; i <= n ; i++) {
            int low = i , high = n , mid , pos = -1 , val = -1;
            while(high - low >= 0) {
                mid = (low + high) >> 1;
                int x = st.query(i , mid);
                if(x <= k) pos = mid , high = mid - 1 , val = x;
                else {
                    low = mid + 1;
                }
            } 
            if(pos != -1) {
                ans = min(ans , abs(k - val));
            } 
        }
        for(int i = 1 ; i <= n ; i++) {
            int low = i , high = n , mid , pos = -1 , val = -1;
            while(high - low >= 0) {
                mid = (low + high) >> 1;
                int x = st.query(i , mid);
                if(x >= k) pos = mid , low = mid + 1 , val = x;
                else {
                    high = mid - 1;
                }
            } 
            if(pos != -1) {
                ans = min(ans , abs(k - val));
            } 
        }
        return ans;
    }
};
