class Solution {
public:

    int invert(int num){
        int ans = 0 ;
        for(int bit=0;bit<30;bit++){
            if(!((num>>bit)&1)) ans += (1<<bit);
        }
        return ans;
    }

    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int a[n];
        for(int i=0;i<n;i++) a[i]=invert(nums[i]);

        set<int>all;
        set<int>prev;
        for(int i=0;i<n;i++){
            set<int>next;
            for(int x: prev){
                int OR = a[i]|x;
                next.insert(OR);
            }
            next.insert(a[i]);
            for(int x: next) all.insert(x);
            prev=next;
        }

        int diff = INT_MAX;
        for(int x: all){
            diff = min(diff,abs(k-invert(x)));
        }

        return diff;
    }
};
