class Solution {
public:
    int _findNum(vector<int>&bits, int l){
        if(l==0)
            return 1e9+1;
        int ans = 0;
        for(int i=0;i<32;i++){
            if(bits[i]==l){
                ans = ans|(1<<i);
            }
        }
        return ans;
    }

    void _addNum(vector<int>&bits,int x){
            int i = 0;
            while(x>0){
                bits[i++] += x%2;
                x/=2;
            }
    }

    void _removeNum(vector<int>&bits,int x){
        int i=0;
        while(x>0){
            bits[i++] -= x%2;
                x/=2;
        }
    }

    int minimumDifference(vector<int>& nums, int k) {
        vector<int>bits(32,0);
        int l = 0,r = 0;

        int ans = 1e9+1;

        while(r<nums.size()){
            if(l==r){
               _addNum(bits,nums[r++]); 
               continue;
            }  

            int x = _findNum(bits,r-l);
            // cout<<x<<" ";
            ans = min(ans,abs(x-k));

            if(x>k)
                _addNum(bits,nums[r++]);
            else
                _removeNum(bits,nums[l++]);

        }
        while(l<nums.size()){
            int x = _findNum(bits,r-l);
            ans = min(ans,abs(x-k));
            _removeNum(bits,nums[l++]);
        }
        int x = _findNum(bits,r-l);
        ans = min(ans,abs(x-k));

        return ans;
    }
};
