class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {

        map< int , set < int >   > m ;

        int r = abs(k-nums[0]) ;

        m[0].ins(nums[0]) ;

        for( int i = 1 ; i < si(nums ) ; i++ )
        {
            // m[i] = m[i-1] ;

            r = min( r , abs( k - nums[i]));
            m[i].ins(nums[i]);

            for( auto &x : m[i-1] )
            {
                int v = x & nums[i] ;

                r = min( r , abs( v - k ) );

                if( !m[i].count(v))
                    m[i].insert(v);
            }
            // print(m[i]);
        }
        return r ;
    }
};
