class Solution {
    int getValue(vector<int>& andVal, int windowSize) {
        // if(windowSize == 0) return 0;
        int ans = 0;
        for(int i=0; i<31; i++) {
            if(andVal[i] >= windowSize) ans |= (1<<i);
        }
        // if(ans < 0) cout << windowSize << " check\n";
        return ans;
    }

    void takeAnd(vector<int>& andVal, int num) {
        for(int i=0; i<31; i++) {
            if((num>>i) & 1) {
                andVal[i]++;
            }
        }
    }
    void remove(vector<int>& andVal, int num) {
        for(int i=0; i<31; i++) {
            if((num>>i) & 1) {
                andVal[i]--;
            }
        }
    }

public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = INT_MAX;
        int i = 0, j = 0;
        vector<int> andVal(32, 0);
        while(j<n) {
            while(j<n && getValue(andVal, j-i) > k) {
                takeAnd(andVal, nums[j]);
                ans = min(ans, abs(getValue(andVal, j-i+1) - k));
                j++;
            }

            if(getValue(andVal, j-i) == k) return 0;

            while(i<j && getValue(andVal, j - i) <= k) {
                remove(andVal, nums[i]);
                i++;
                ans = min(ans, abs(k - getValue(andVal, j-i)));
            }
        }

        return ans;
    }
};
