#define vi vector<int>

struct SegTree {
    vector<int> t;
    int n;

    SegTree(int n) {
        this->n = n;
        t.assign(2*n, 0);
    }

    SegTree(const vector<int> &a) {
        this->n = a.size();
        t.assign(2*n, 0);
        for (int i = 0; i < n; i++) {
            t[n+i] = a[i];
        }
        build();
    }

    void build() {
        for (int i = n-1; i>0; --i) t[i] = t[i << 1] & t[i << 1 | 1];
    }
    void modify(int p, int value){
        for (t[p += n] = value; p>1; p>>=1) t[p>>1] = t[p] & t[p^1];
    }
    int query(int l, int r){
        r++;
        int res = pow(2,30)-1;
        for (l += n, r += n; l<r; l>>=1, r>>=1){
            if(l&1) res &= t[l++];
            if(r&1) res &= t[--r];
        }
        return res;
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        SegTree t(nums);
        int ans = abs(k-nums[0]);
        for (int i = 0; i < n; i++) {
            int l = i;
            int r = n-1;
            while (l <= r) {
                int m = (l+r)/2;
                int s = t.query(i, m);
                ans = min(ans, abs(k-s));
                if (s >= k) {
                    l = m+1;
                } else {
                    r = m-1;
                }
            }
        }
        return ans;
    }
};
