class Solution {
public:
    void add(vector<int> &f,int num, int d) {
        for(int i=0;i<31;i++) {
            if(num&(1<<i)) {
                f[i]+=d;
            }
        }
        return;
    }

    int getNum(vector<int> &f,int l) {
        int ans=0;
        for(int i=0;i<31;i++) {
            if(f[i]==l) {
                ans |= (1<<i);
            }
        }
        return ans;
    }
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> freq(32,0);
        add(freq,nums[0],1);
        int i=1,j=0, ans = abs(k-nums[0]);
        int len=1;
        while(i<nums.size()) {
            int res = getNum(freq,len);
            ans=min(ans,abs(k-res));
            if(getNum(freq,len)<=k) {
                add(freq,nums[j],-1);
                j++;
                len--;
            } else {
                add(freq,nums[i],1);
                i++;
                len++;
            }
        }
        while(j<nums.size()) {
            int res = getNum(freq,len);
            ans=min(ans,abs(k-res));
            add(freq,nums[j],-1);
            len--;
            j++;
        }
        int res = getNum(freq,len);
        ans=min(ans,abs(k-res));
        return ans;
    }
};
